import os
import json
from collections import defaultdict


base_dir = os.path.dirname(os.path.abspath(__file__))
data_file_dir = os.path.join(base_dir, "data")
pet_phrase_file_path = os.path.join(data_file_dir, "pet_phrases.txt")

# pet_phrase_table = list()
# with open(pet_phrase_file_path, mode="r", encoding="utf8") as fr:
#     for word in fr:
#         pet_phrase_table.append(word.strip())

pet_phrase_table = ["呃", "哎", "哎呦", "就是说", "好", "我们说", "换句话说", "来吧", "好吧", "对吧", "诶", "好勒"]

def discover_pet_phrase(fields):
    pet_phrase_threshold = 3
    pet_phrase_dict = defaultdict(int)
    detail = defaultdict(list)
    for field in fields:
        for phrase in pet_phrase_table:
            phrase_cnt = field['text'].count(phrase)
            if phrase_cnt > 0:
                new_field = field.copy()
                new_field["count"] = phrase_cnt
                pet_phrase_dict[phrase] += phrase_cnt
                detail[phrase].append(new_field)
    pet_phrase_record = dict()
    for phrase in pet_phrase_dict:
        phrase_cnt = pet_phrase_dict[phrase]
        if phrase_cnt >= pet_phrase_threshold:
            pet_phrase_record[phrase] = {"count": phrase_cnt,
                                         "detail": detail[phrase]}
    return pet_phrase_record

def discover_pet_phrase_api(asr_result):
    asr_result = json.loads(asr_result)["data"]["result"]
    return_pet_phrases_dict = dict()
    query_pet_phrases_dict = discover_pet_phrase(asr_result)
    for phrase in query_pet_phrases_dict:
        if query_pet_phrases_dict[phrase]["count"] > 0:
            details = query_pet_phrases_dict[phrase]["detail"]
            return_pet_phrases_dict[phrase] = list()
            for detail in details:
                tmp_detail = {"begin_time": detail["begin_time"],
                              "end_time": detail["end_time"],
                              "count": detail["count"]}
                return_pet_phrases_dict[phrase].append(tmp_detail)
    return json.dumps(return_pet_phrases_dict)


